mapper.select:

		SELECT * 
			FROM (
			SELECT 
				/*+INDEX_DESC(tbl_board PK_BOARD) */ 
				ROWNUM rn, no, title, writer, content, regidate, replycnt, level, mgr
				FROM tbl_board 
				WHERE



		rownum <= #{pageNumber} * #{amount}
		START WITH mgr is null
        CONNECT BY PRIOR no = mgr
		ORDER SIBLINGS by no desc
		) 
		WHERE rn > (#{pageNumber} -1) * #{amount}


SELECT no, title, writer, content, regidate
	FROM tbl_board
	WHERE no = #{no}

mapper.update:
UPDATE tbl_board 
	SET title = #{title}, content = #{content}, writer = #{writer}
	WHERE no = #{no}

UPDATE tbl_board 
	SET replyCnt = replyCnt + #{amount} 
	WHERE no = #{no}
