mapper.select:

SELECT no, board_no, reply, replyer, replydate
	FROM (
		SELECT /*+INDEX_DESC(tbl_reply PK_REPLY) */ ROWNUM rn, tbl_reply.*
	    	FROM tbl_reply
	    	WHERE board_no = #{bno}
	    	AND no > 0
	    	AND rownum <= #{cri.pageNumber} * #{cri.amount}
	) 
	WHERE rn > (#{cri.pageNumber} -1) * #{cri.amount}


SELECT count(no) 
	FROM tbl_reply 
	WHERE board_no=#{board_no}

mapper.update:
UPDATE tbl_reply 
	SET reply = #{reply}, replyer = #{replyer}
	WHERE no=#{no}
